commits.content:
import styled from 'styled-components';

export const SearchContainer = styled.div`
    height: 30px;
    width: 270px;
    display: flex;
    align-items: center;

    input {
        width: 100%;
        height: 30px;
        border: none;
        border-bottom: 2px solid #635e5e;
        padding: 5px 5px 5px 10px;
        background: none;
        color: #000;

        &:focus {
            outline: none;
            box-shadow: none;
        }
        &::placeholder {
            font-family: 'Roboto', sans-serif;
        }
    }
    i {
        position: relative;
        left: -25px;
        top: 1px;
        color: #635e5e;
        width: 0;
    }
`;
